### Calculate a polygon around a point with a given distance radius#### make sure to convert this to UTM first with WGS84 datumlibrary(rgeos)foo <- function(lat, lon, ...){  latlon <- paste(lon, lat)  #   poly <- readWKT(sprintf("POLYGON((%s,%s,%s,%s))", latlon,latlon,latlon,latlon))  poly <- readWKT(sprintf("POLYGON((%s,%s,%s,%s))", latlon,latlon,latlon,latlon))  g <- gBuffer(poly, ...)  writeWKT(g)}foo(lat=33.95, lon=-118.40, width=0.2)# this was Barry's solutionrequire(sp)require(rgeos)require(rgdal)d <- data.frame(lat=c(33.95,34.95,34.70), lon=c(-118.40,-118.22,-118.43),ID=1:3)coordinates(d)=~lon+latproj4string(d)=CRS("+init=epsg:4326")buf <- gBuffer(d,width=0.2)writeWKT(buf[1])plot(buf)# d <- cbind(c(33.95,34.95,34.70), c(-118.40,-118.22,-118.43))# coordinates(d)=~lon+lat# spTransform(d, CRS("+proj=utm +zone=11 +datum=WGS84"))# project(d, "+proj=utm +zone=11 +datum=WGS84")# library(rgdal) # xy <- cbind(c(118, 119), c(10, 50)) # project(xy, "+proj=utm +zone=24 ellps=WGS84") # spTransform(xy, CRS("+proj=utm +zone=51 ellps=WGS84"))### Get UTM zone for a set of lat/long coordinates#### for western hemisphere only long2UTM <- function(long) {  (floor((long + 180)/6) %% 60) + 1}long2UTM(4)#### for globelong2utm <- function(lon, lat) {  if(56 <= lat & lat < 64){    if(0 <= lon & lon < 3){ 31 } else       if(3 <= lon & lon < 12) { 32 } else { NULL }  } else   if(72 <= lat) {    if(0 <= lon & lon < 9){ 31 } else       if(9 <= lon & lon < 21) { 33 } else         if(21 <= lon & lon < 33) { 35 } else           if(33 <= lon & lon < 42) { 37 } else { NULL }  }  (floor((lon + 180)/6) %% 60) + 1}long2utm(-60, 65)## library(rgdal)## Create an example SpatialPoints object# pts <- SpatialPoints(cbind(-120:-121, 39:40), #                      proj4string = CRS("+proj=longlat +datum=NAD27"))## Construct a proper proj4string# UTM11N <- "+proj=utm +zone=11 +datum=NAD83 +units=m +no_defs"# UTM11N <- paste(UTM11N